ALTER TABLE DETTURNO ADD CONSTRAINT CHKDETTURNO CHECK (
NOT EXISTS(Select *
           from detturno d1
           where Exists(Select *
                        from turno t
                        where d1.turnoid=t.turnoid and
                              t.profid=(Select profid
                                        from turno ts
                                        where ts.turnoid=new.turnoid) and
                              t.lugserid=(Select lugserid
                                        from turno ts
                                        where ts.turnoid=new.turnoid)          ) and
                 d1.turnoid=new.turnoid and d1.detturnoid <> new.detturnoid and
                 d1.lugserid=new.lugserid and d1.fechatra=new.fechatra and
                 ( (((d1.hi1<=new.hi1 AND d1.hF1>=new.hf1 and d1.act1="0") or
                  (d1.hi2<=new.hi1 AND d1.hF2>=new.hf1 and d1.act2="0") or
                  (d1.hi3<=new.hi1 AND d1.hF3>=new.hf1 and d1.act3="0")) or
                  ((d1.hi1<=new.hi2 AND d1.hF1>=new.hf2 and d1.act1="0") or
                  (d1.hi2<=new.hi2 AND d1.hF2>=new.hf2 and d1.act2="0") or
                  (d1.hi3<=new.hi2 AND d1.hF3>=new.hf2 and d1.act3="0")) or
                  ((d1.hi1<=new.hi3 AND d1.hF1>=new.hf3 and d1.act1="0") or
                  (d1.hi2<=new.hi3 AND d1.hF2>=new.hf3 and d1.act2="0") or
                  (d1.hi3<=new.hi3 AND d1.hF3>=new.hf3 and d1.act3="0")) ))
          )
);
ALTER TABLE CITA ADD CONSTRAINT CHKCITA CHECK (
NOT EXISTS(select *
           from turinac t
           where t.fecha=new.fecha AND
                 t.lugserid=new.lugserid AND
                 t.sucid=new.sucid AND
                 t.profid=new.profid AND
                 t.hi<=new.horaini AND
                 t.hf>=new.horaini )
);


