/* Trigger: REPUCREPNIIF_BIU_CUENTA */
CREATE TRIGGER REPUCREPNIIF_BIU_CUENTA FOR REPUCREPNIIF
ACTIVE BEFORE INSERT POSITION 1
AS
declare variable vcodctacli varchar(16);
declare variable vitem varchar(3);
declare variable vrepoid integer;

begin
select pp.codigo from plancuentas pp where pp.pucid=new.pucid into :vcodctacli;
select drp.reportid from dereporteniif drp where drp.derepniifid=new.derepniifid into :vrepoid ;

if ( exists (select first 1 codigo,item valor from
(select dr.item, p.codigo from reporteniif r, dereporteniif dr, repucrepniif rp , plancuentas p
where r.reportid=dr.reportid and dr.derepniifid=rp.derepniifid and rp.pucid=p.pucid
and iif(char_length(:vcodctacli)<char_length(p.codigo),iif(substring(p.codigo from 1 for char_length(:vcodctacli))=:vcodctacli,1,0),
iif(substring(:vcodctacli from 1 for char_length(p.codigo))=p.codigo,1,0))=1
and r.reportid=:vrepoid order by dr.item)a)) then
begin
select first 1 item valor from
(select dr.item, p.codigo from reporteniif r, dereporteniif dr, repucrepniif rp , plancuentas p
where r.reportid=dr.reportid and dr.derepniifid=rp.derepniifid and rp.pucid=p.pucid
and iif(char_length(:vcodctacli)<char_length(p.codigo),iif(substring(p.codigo from 1 for char_length(:vcodctacli))=:vcodctacli,1,0),
iif(substring(:vcodctacli from 1 for char_length(p.codigo))=p.codigo,1,0))=1
and r.reportid=:vrepoid order by dr.item)a into :vitem;

exception tns_valor_niif ' No se puede Insertar la cuenta '||:vcodctacli||' Ya esta en el Item '||:vitem||' ';
end

END
^

