/* Trigger: TNS_DEKARD_BU */
CREATE TRIGGER TNS_DEKARD_BU FOR DEKARDEX
INACTIVE BEFORE UPDATE POSITION 0
AS
declare variable vActvar varchar(1);
declare variable vCodComp varchar(2);
declare variable vcodpre varchar(2);
declare variable vnumkdx varchar(8);
declare variable vFecAsent varchar(10);

BEGIN
select CONTENIDO from VARIOS where VARIAB='VACTVAL' into :vActvar;
IF ((:vActvar='') or (:vActvar is null) or (:vActvar='S') ) then
IF (OLD.MATID<>NEW.MATID) THEN
EXCEPTION TNS_DKX_CAMBIOCODIGO 'NO SE PUEDE CAMBIAR CODIGO ';

select codcomp from kardex where kardexid=new.kardexid into :vcodcomp;
select codprefijo from kardex where kardexid=new.kardexid into :vcodpre;
select numero from kardex where kardexid=new.kardexid into :vnumkdx;
select FECASENTAD from kardex where kardexid=new.kardexid into :vFecAsent;

if ((:vcodcomp='RS') or (:vcodcomp='RE')) then
begin
if (new.REMTOTFAC>new.canmat) then
begin
exception tns_dkx_negativo ' Sin saldo en Remisiones ';
end
end

if (:vFecAsent<>'') then
begin
if  ((NEW.CANLISTA<>OLD.CANLISTA) or (NEW.CANMAT<>OLD.CANMAT) or (OLD.PRECIOBASE<>NEW.PRECIOBASE) or (OLD.PRECIOIVA<>NEW.PRECIOIVA))  then
begin
exception tns_dkx_negativo ' Movimiento '||vcodcomp||vcodpre||vnumkdx||'  asentado no se puede cambiar Valores ';
end
end

END
^

