ALTER PROCEDURE SP_PROCESAR_RECIBO (
    P_CODCOMP VARCHAR(10),
    P_CODPREFIJO VARCHAR(2),
    P_NUMERO VARCHAR(8))
RETURNS (
    RESULTADO VARCHAR(100),
    MENSAJE VARCHAR(500))
AS
DECLARE VARIABLE V_RECIBOID INTEGER;
DECLARE VARIABLE V_FECASENT TIMESTAMP;
DECLARE VARIABLE V_FECHA TIMESTAMP;
DECLARE VARIABLE V_PERIODO VARCHAR(10);
DECLARE VARIABLE V_TERID INTEGER;
DECLARE VARIABLE V_MOVID INTEGER;
DECLARE VARIABLE V_DEDOCUMID INTEGER;
DECLARE VARIABLE V_VALOR NUMERIC(15,2);
DECLARE VARIABLE V_SALDO NUMERIC(15,2);
DECLARE VARIABLE V_TOTAL NUMERIC(15,2);
DECLARE VARIABLE V_CONTADOR INTEGER;
DECLARE VARIABLE V_ERROR VARCHAR(500);
DECLARE VARIABLE V_PROCESAR_ASENTAMIENTO INTEGER;
DECLARE VARIABLE V_BCOID INTEGER;
DECLARE VARIABLE V_PUCID INTEGER;
DECLARE VARIABLE V_PUCID_CREDITO INTEGER;
DECLARE VARIABLE V_CODIGO_CUENTA VARCHAR(50);
DECLARE VARIABLE V_EXISTE_MOVI INTEGER;
BEGIN
    RESULTADO = 'ERROR';
    MENSAJE = '';
    V_ERROR = '';
    V_CONTADOR = 0;
    /* Buscar el recibo */
    SELECT RECIBOID, FECASENT, FECHA, PERIODO, TERID, TOTAL
    FROM RECIBO
    WHERE CODCOMP = :P_CODCOMP
      AND CODPREFIJO = :P_CODPREFIJO
      AND NUMERO = :P_NUMERO
    INTO :V_RECIBOID, :V_FECASENT, :V_FECHA, :V_PERIODO, :V_TERID, :V_TOTAL;
    /* Validar si existe el recibo */
    IF (V_RECIBOID IS NULL) THEN
    BEGIN
        RESULTADO = 'ERROR';
        MENSAJE = 'No existe el recibo con los datos proporcionados';
        SUSPEND;
        EXIT;
    END
    /* Determinar si es asentamiento o desasentamiento */
    IF (V_FECASENT IS NULL) THEN
        V_PROCESAR_ASENTAMIENTO = 1;
    ELSE
        V_PROCESAR_ASENTAMIENTO = 0;
    /* VALIDACIONES ADICIONALES SOLO PARA ASENTAMIENTO */
    IF (V_PROCESAR_ASENTAMIENTO = 1) THEN
    BEGIN
        /* Validar que no exista registro en MOVI */
        SELECT COUNT(*)
        FROM MOVI
        WHERE CODCOMP = :P_CODCOMP
          AND CODPREFIJO = :P_CODPREFIJO
          AND NUMERO = :P_NUMERO
        INTO :V_EXISTE_MOVI;
        IF (V_EXISTE_MOVI > 0) THEN
        BEGIN
            RESULTADO = 'ERROR';
            MENSAJE = 'Ya existe un registro en MOVI con estos datos';
            SUSPEND;
            EXIT;
        END
        /* Obtener el codigo de cuenta desde VARIOS */
        SELECT CONTENIDO
        FROM VARIOS
        WHERE VARIAB = 'GCTACLI'
        INTO :V_CODIGO_CUENTA;
        IF (V_CODIGO_CUENTA IS NULL OR V_CODIGO_CUENTA = '') THEN
        BEGIN
            RESULTADO = 'ERROR';
            MENSAJE = 'No se encontro la variable GCTACLI en tabla VARIOS';
            SUSPEND;
            EXIT;
        END
        /* Obtener PUCID desde PLANCUENTAS */
        SELECT PUCID
        FROM PLANCUENTAS
        WHERE PUCID = :V_CODIGO_CUENTA
        INTO :V_PUCID_CREDITO;
        IF (V_PUCID_CREDITO IS NULL) THEN
        BEGIN
            RESULTADO = 'ERROR';
            MENSAJE = 'No se encontro el codigo de cuenta ' || V_CODIGO_CUENTA || ' en PLANCUENTAS';
            SUSPEND;
            EXIT;
        END
        /* Validar bancos - PUCID no debe ser NULL */
        FOR SELECT DF.BCOID
            FROM DERECIFP DF
            WHERE DF.RECIBOID = :V_RECIBOID
            INTO :V_BCOID
        DO
        BEGIN
            SELECT PUCID
            FROM BANCO
            WHERE BCOID = :V_BCOID
            INTO :V_PUCID;
            IF (V_PUCID IS NULL) THEN
            BEGIN
                RESULTADO = 'ERROR';
                MENSAJE = 'El banco ID ' || V_BCOID || ' no tiene PUCID definido';
                SUSPEND;
                EXIT;
            END
        END
    END
    /* PRIMERA FASE: VALIDACION DE SALDOS */
    FOR SELECT DR.DEDOCUMID, DR.VALOR
        FROM DERECIBO DR
        WHERE DR.RECIBOID = :V_RECIBOID
        INTO :V_DEDOCUMID, :V_VALOR
    DO
    BEGIN
        /* Obtener el saldo actual del documento */
        SELECT SALVAL
        FROM DEDOCUM
        WHERE DEDOCUMID = :V_DEDOCUMID
        INTO :V_SALDO;
        IF (V_PROCESAR_ASENTAMIENTO = 1) THEN
        BEGIN
            /* Validar para asentamiento */
            IF (V_SALDO < V_VALOR) THEN
            BEGIN
                V_ERROR = 'El documento ID ' || V_DEDOCUMID || ' no tiene saldo suficiente';
                RESULTADO = 'ERROR';
                MENSAJE = V_ERROR;
                SUSPEND;
                EXIT;
            END
        END
        V_CONTADOR = V_CONTADOR + 1;
    END
    /* Validar que hay registros para procesar */
    IF (V_CONTADOR = 0) THEN
    BEGIN
        RESULTADO = 'ERROR';
        MENSAJE = 'No se encontraron registros en DERECIBO para procesar';
        SUSPEND;
        EXIT;
    END
    /* SEGUNDA FASE: CREACION DE REGISTRO EN MOVI (solo para asentamiento) */
    IF (V_PROCESAR_ASENTAMIENTO = 1) THEN
    BEGIN
        INSERT INTO MOVI (CODCOMP, CODPREFIJO, NUMERO, FECHA, PERIODO, TOTDB, TOTCR, AREADID, SUCID)
        VALUES (:P_CODCOMP, :P_CODPREFIJO, :P_NUMERO, :V_FECHA, :V_PERIODO, :V_TOTAL, :V_TOTAL, 1, 1);
        /* Obtener el ID del registro recien creado */
        SELECT MOVID
        FROM MOVI
        WHERE CODCOMP = :P_CODCOMP
          AND CODPREFIJO = :P_CODPREFIJO
          AND NUMERO = :P_NUMERO
        INTO :V_MOVID;
        /* Crear UN SOLO registro en DEMOVI con la suma total (CREDITO) */
        INSERT INTO DEMOVI (MOVID, PUCID, VALORTRA, TERID, TIPOCD, CENID, FUENTERECID)
        VALUES (:V_MOVID, :V_PUCID_CREDITO, :V_TOTAL, :V_TERID, 'C', 1, 1);
        /* Crear registros en DEMOVI por cada DERECIFP (DEBITOS) */
        FOR SELECT DF.BCOID, DF.VALOR
            FROM DERECIFP DF
            WHERE DF.RECIBOID = :V_RECIBOID
            INTO :V_BCOID, :V_VALOR
        DO
        BEGIN
            /* Obtener PUCID del banco */
            SELECT PUCID
            FROM BANCO
            WHERE BCOID = :V_BCOID
            INTO :V_PUCID;
            /* Insertar en DEMOVI - Debito */
            INSERT INTO DEMOVI (MOVID, PUCID, VALORTRA, TERID, TIPOCD, CENID, FUENTERECID)
            VALUES (:V_MOVID, :V_PUCID, :V_VALOR, :V_TERID, 'D', 1, 1);
        END
    END
    ELSE
    BEGIN
        /* Para desasentamiento: eliminar registros de MOVI y DEMOVI */
        SELECT MOVID
        FROM MOVI
        WHERE CODCOMP = :P_CODCOMP
          AND CODPREFIJO = :P_CODPREFIJO
          AND NUMERO = :P_NUMERO
        INTO :V_MOVID;
        IF (V_MOVID IS NOT NULL) THEN
        BEGIN
            DELETE FROM DEMOVI WHERE MOVID = :V_MOVID;
            DELETE FROM MOVI WHERE MOVID = :V_MOVID;
        END
    END
    /* TERCERA FASE: ACTUALIZACION DE SALDOS */
    FOR SELECT DR.DEDOCUMID, DR.VALOR
        FROM DERECIBO DR
        WHERE DR.RECIBOID = :V_RECIBOID
        INTO :V_DEDOCUMID, :V_VALOR
    DO
    BEGIN
        IF (V_PROCESAR_ASENTAMIENTO = 1) THEN
        BEGIN
            /* ASENTAMIENTO */
            UPDATE DEDOCUM
            SET SALVAL = SALVAL - :V_VALOR,
                FECULPAGO = :V_FECHA
            WHERE DEDOCUMID = :V_DEDOCUMID;
        END
        ELSE
        BEGIN
            /* DESASENTAMIENTO */
            UPDATE DEDOCUM
            SET SALVAL = SALVAL + :V_VALOR,
                FECULPAGO = NULL
            WHERE DEDOCUMID = :V_DEDOCUMID;
        END
    END
    /* CUARTA FASE: Actualizar FECASENT del recibo */
    IF (V_PROCESAR_ASENTAMIENTO = 1) THEN
    BEGIN
        UPDATE RECIBO
        SET FECASENT = :V_FECHA
        WHERE RECIBOID = :V_RECIBOID;
        RESULTADO = 'EXITO';
        MENSAJE = 'Recibo asentado correctamente';
    END
    ELSE
    BEGIN
        UPDATE RECIBO
        SET FECASENT = NULL
        WHERE RECIBOID = :V_RECIBOID;
        RESULTADO = 'EXITO';
        MENSAJE = 'Recibo desasentado correctamente';
    END
 SUSPEND;
EXIT; END^


