ALTER PROCEDURE TNS_INS_PEDIDOVENTA (
    VCODCOMP VARCHAR(2),
    VCODPREFIJO VARCHAR(2),
    VNUMERO VARCHAR(8),
    VFECHA VARCHAR(10),
    VFORMAPAGO VARCHAR(2),
    VNITCLIENTE VARCHAR(20),
    VNITVENDEDOR VARCHAR(20),
    VNITDESPACHAR VARCHAR(20),
    VBANCO VARCHAR(4),
    VMODO VARCHAR(1),
    VUSUARIO VARCHAR(20))
RETURNS (
    VRESULTADO VARCHAR(250))
AS
declare variable VCODPREFIJOAUX varchar(2);
declare variable VPERIODO varchar(2);
declare variable VTERIDCLIENTE integer;
declare variable VTERIDVENDEDOR integer;
declare variable VTERIDDESPACHAR integer;
declare variable VBANCOID integer;
declare variable VFECVENCE date;
declare variable VPLAZOPDIAS integer;
begin

if (VCODPREFIJO = '') then
begin
VRESULTADO = 'Debe asignar un prefijo al Pedido de Venta';
suspend;
exit;
end

select codprefijo
from prefijo
where codprefijo = :VCODPREFIJO
into :VCODPREFIJOAUX;

if (VCODPREFIJOAUX is null) then
begin
VRESULTADO = 'Prefijo de Pedido de Venta NO ES VALIDO';
suspend;
exit;
end

if (VNUMERO='') then
begin
VRESULTADO = 'Debe asignar un Numero al Pedido de Venta';
suspend;
exit;
end

if (cast(VFECHA as VARCHAR(10)) = '') then
begin
VRESULTADO = 'Debe asignar una Fecha al Pedido de Venta';
suspend;
exit;
end

VPERIODO = lpad(extract(month from cast(VFECHA as timestamp)),2,0);


select TERID
from TERCEROS
where NIT = :VNITCLIENTE
into :VTERIDCLIENTE;

if (VTERIDCLIENTE is null) then
begin
VRESULTADO = 'Cliente NO EXISTE';
suspend;
exit;
end


select TERID
from TERCEROS
where NIT = :VNITVENDEDOR
into :VTERIDVENDEDOR;

if (VTERIDVENDEDOR is null) then
begin
VRESULTADO = 'Vendedor NO EXISTE';
suspend;
exit;
end


select TERID
from TERCEROS
where NIT = :VNITDESPACHAR
into :VTERIDDESPACHAR;

if (VTERIDDESPACHAR is null) then
begin
VRESULTADO = 'Despachar A, NO EXISTE';
suspend;
exit;
end

   
if (VFORMAPAGO = 'CO') then
begin

select Bcoid
from Banco
where CODIGO = :VBANCO
into :VBANCOID;

if (VBANCOID is null) then
begin
VRESULTADO = 'Banco NO EXISTE';
suspend;
exit;
end

end
else
begin
if (VFECVENCE = '') then
begin
VRESULTADO = 'Debe asignar la Fecha de Vencimiento';
suspend;
exit;
end
end

VPLAZOPDIAS=1;

if (VMODO='I') then 
begin

execute procedure TNS_INS_FACTURAVTA(VCODCOMP,VCODPREFIJO,VNUMERO,VFECHA,VPERIODO,VFORMAPAGO,VPLAZOPDIAS,VUSUARIO,VBANCO,VNITCLIENTE,VNUMERO,2)
returning_values :VRESULTADO;
if (VRESULTADO <> 'OK') then
begin
suspend;
exit;
end
VRESULTADO = 'OK';
suspend;
end
end^


