    IDEKARDEXID INTEGER,
    IMODO SMALLINT)
RETURNS (
    OMENSAJE BLOB SUB_TYPE 0 SEGMENT SIZE 80)
AS
declare variable VMENSAJE blob sub_type 0 segment size 80 = '';
declare variable VMATID integer;
declare variable VBODID integer;
declare variable VSERIALES blob sub_type 0 segment size 80;
declare variable VCANMAT decimal(15,2);
declare variable VCODCOMP varchar(2);
declare variable VSERIAL varchar(40);
declare variable VPOS integer = 0;
declare variable VSALIR integer = 0;
declare variable VMOV varchar(10);
declare variable VSERIALID integer;
declare variable VSALDO integer;
declare variable VCODIGO varchar(25);
declare variable VVALIDARSIEXISTIO integer = 0;
declare variable VBORRARQUEEXISTIO integer = 0;
declare variable VEXISTESERIAL integer;
declare variable VCANTREGISTROS integer;
declare variable VMANLINTEL varchar(1);
declare variable VFILTROSERIAL varchar(100);
declare variable VSQLCAD varchar(300);
declare variable VSERIALAUX varchar(50);
declare variable VREMISIONID integer = null;
declare variable VNOREPSEREXI varchar(1);
BEGIN

--la relacion dekardex - dekardexself se hace inner porque
--si llega hasta este punto es porque trae serial en la self
select d.matid, d.bodid, ds.seriales, d.canmat, k.codcomp, m.codigo, ds.remisionid
from dekardex d inner join kardex k on d.kardexid = k.kardexid
inner join dekardexself ds on d.dekardexid = ds.dekardexid
inner join material m on d.matid = m.matid
where d.dekardexid = :IDEKARDEXID
into :vmatid,:vbodid,:vseriales,:vcanmat,:vcodcomp,:vcodigo,:vremisionid;

--maneja lineas telefonicas
SELECT CONTENIDO FROM Varios WHERE variab = "GMANLINTEL" INTO :vmanlintel;
--valida serial unico para cada articulo
SELECT CONTENIDO FROM Varios WHERE variab = "GNOREPSEREXI" INTO :vnorepserexi;

--..........determina si suma o resta al inventario
vmov = 'ENTRADA';
if (vcodcomp = 'FV' or vcodcomp = 'RS' or vcodcomp = 'CO' or vcodcomp = 'OC' or vcodcomp = 'DC' or ((vcodcomp = 'TR' or vcodcomp = 'NI') and vcanmat < 0 )) then
begin
if (IMODO = 1) then
vmov = 'SALIDA';
end
else
begin
if (not(IMODO = 1)) then
vmov = 'SALIDA';
end

--...........un serial no puede existir en el tiempo mas de una vez en el mismo articulo
if ((vnorepserexi = 'S') and (vmov = 'ENTRADA') and ((vcodcomp = 'FC') or (vcodcomp = 'RE' and vremisionid is null) or ( vcodcomp = "NI" and vcanmat > 0 )))  then
vvalidarsiexistio = 1;

if ((vmov = 'SALIDA') and (vcodcomp = 'FC' or vcodcomp = 'DC' or vcodcomp = 'RE' or vcodcomp = 'TR' or ( vcodcomp = 'NI' and vcanmat > 0 ))) then
vborrarqueexistio = 1;

--.........................

while (vsalir = 0) do --recorre blob seriales manipulando serial por serial
begin

vpos = position(',',vseriales);
if (vpos = 0) then
begin
vsalir = 1;
vserial = vseriales;
end
else
begin
vserial = substring(vseriales from 1 for vpos - 1);
vseriales = substring(vseriales from vpos + 1 for char_length(vseriales) - vpos);
end

--maneja linea telefonica
vfiltroserial = ' and nroserial = "'||vserial||'" ';
if ((vmanlintel = 'S') and (position(';',vserial) <> 0)) then
begin
vserialaux = substring(vserial FROM 1 for position(';',vserial) - 1);
vfiltroserial = ' and (nroserial = "'||vserial||'" or nroserial = "'||vserialaux||'") ';
end

--.....................................
vserialid = null;
vsqlcad = 'select serialid, coalesce(saldo,0)
from serial
where matid = '||vmatid||' and bodid = '||vbodid||' '||vfiltroserial||' ';
execute statement vsqlcad into :vserialid, :vsaldo;

if (vvalidarsiexistio = 1) then
begin
vexisteserial = 0;
vcantregistros = 0;
vsqlcad = 'select first 1 serialid
from serial
where matid = '||vmatid||' '||vfiltroserial||' ';
execute statement vsqlcad into :vcantregistros;

if (vcantregistros > 0) then
vexisteserial = 1;
end

if (vmov = 'ENTRADA') then
begin
if ((vserialid is not null) and (vsaldo > 0)) then
vmensaje = vmensaje||'ARTICULO: '||vcodigo||' SERIAL: '||vserial||' ya existe.'||ASCII_CHAR(13)||ASCII_CHAR(10);
else
begin
if (vserialid is null) then
INSERT INTO serial (matid,bodid,nroserial,saldo) VALUES (:vmatid,:vbodid,:vserial,1);
else
begin
if ((vvalidarsiexistio = 1) and (vexisteserial = 1)) then
vmensaje = vmensaje||'ARTICULO: '||vcodigo||' SERIAL: '||vserial||' ya existio.'||ASCII_CHAR(13)||ASCII_CHAR(10);
else
UPDATE serial SET saldo = 1 WHERE serialid = :vserialid;
end
end
end
else
begin
if ((vserialid is null) or (vsaldo = 0)) then
vmensaje = vmensaje||'ARTICULO: '||vcodigo||' SERIAL: '||vserial||' no existe.'||ASCII_CHAR(13)||ASCII_CHAR(10);
else
begin
if (vborrarqueexistio = 1) then
DELETE FROM serial WHERE serialid = :vserialid;
else
UPDATE serial SET saldo = 0 WHERE serialid = :vserialid;
end
end


end

OMENSAJE = vmensaje;
SUSPEND;

end^


