ALTER PROCEDURE TNS_REPORTES_NIIF (
    VSUCURSALINI CHAR(2),
    VSUCURSALFIN CHAR(2),
    FECHAINI VARCHAR(10),
    FECHACORTE VARCHAR(10),
    SERVI_ANTERIOR VARCHAR(120),
    RUTA_ANTERIOR VARCHAR(240),
    CODREPORTE VARCHAR(10))
RETURNS (
    ITEMCALCULA CHAR(1),
    ITEMACUMULA CHAR(1),
    NOTAS VARCHAR(120),
    VALOR_ANT NUMERIC(15,2),
    VALOR NUMERIC(15,2),
    ITEMDESCRIP VARCHAR(240),
    REPDESCRIP VARCHAR(240),
    ITEM VARCHAR(10))
AS
declare variable VSUCFIN varchar(2);
declare variable VSUCINI varchar(2);
declare variable J integer;
declare variable VVALACT numeric(15,2);
declare variable VSIGNO char(1);
declare variable VVALORCALC numeric(15,2);
declare variable I integer;
declare variable VITECALCULA varchar(10);
declare variable VACUMULA char(1);
declare variable VCALCULA char(1);
declare variable VCALC varchar(50);
declare variable VITEMM varchar(10);
declare variable VREPORTEID integer;
declare variable VPORCENTAJE numeric(5,2);
declare variable VTIPONIIF char(1);
declare variable VVALORTRA varchar(50);
declare variable QGENERAL varchar(5000);
BEGIN
if (codreporte<>"") then
begin
select REPORTID FROM REPORTENIIF where CODIGOREP=:codreporte into :vreporteid;
if (vreporteid IS null)  then
begin
NOTAS = "ERROR";
ITEMDESCRIP="Reporte no Existe";
suspend;
exit;
end

select codsuc from sucursal where codsuc=:vsucursalini into :vsucini;
if (vsucini is null) then
begin
NOTAS = "ERROR";
ITEMDESCRIP="Sucursal Inicial no Existe";
suspend;
exit;
end

select codsuc from sucursal where codsuc=:vsucursalfin into :vsucfin;
if (vsucfin is null) then
begin
NOTAS = "ERROR";
ITEMDESCRIP="Sucursal Final no Existe";
suspend;
exit;
end

select TIPONIIF FROM REPORTENIIF where CODIGOREP=:codreporte into :VTIPONIIF;
if (VTIPONIIF="S") then
VVALORTRA="dm.VALORTRANIIF";
else
VVALORTRA="dm.valortra";
end
else
begin
NOTAS = "ERROR";
ITEMDESCRIP="Digite Codigo de Reporte";
suspend;
exit;
end
FOR SELECT DR.ITEM,DR.CALCULO,DR.CALCULA,DR.ACUMULA,DR.PORCENTAJE
FROM REPORTENIIF R,DEREPORTENIIF DR WHERE R.REPORTID=DR.REPORTID AND R.REPORTID=:vreporteid
Order by dr.item into :vitemm,:vcalc,:vcalcula,:vacumula,:VPORCENTAJE do
begin
if (vacumula="S") then
begin

QGENERAL='select r.descripcion, dr.item,dr.descrip,dr.notas,dr.calcula,dr.acumula, sum(iif(a.operac="+",a.val,a.val*(-1)))valori '||
'from REPORTENIIF R, dereporteniif dr left join '||
'(select rp.pucid,rp.derepniifid,rp.operac,p1.codigo,sum(movs.valor) val    from repucrepniif rp,'||
'plancuentas p1 inner join    (select P.CODIGO,'||
'sum(iif(dm.tipocd is null, 0,iif(( dm.tipocd="D" and (substring(P.CODIGO from 1 for 1) in("1","5","6","7")) )or ( dm.tipocd="C" and (substring(P.CODIGO from 1 for 1) '||
'in("2","3","4"))),'||VVALORTRA||',(-1)*'||VVALORTRA||'))) valor '||
'from plancuentas p left join demovi dm On dm.pucid=p.pucid inner join movi m ON    (m.movid=dm.movid) inner join sucursal s on (m.sucid=s.sucid) and m.fecha>="'||fechaini||'" '||
' and m.fecha<="'||fechacorte||'" and m.fecasent is not null and s.codsuc>="'||vsucini||'" and s.codsuc>="'||vsucfin||'"  Group by 1) movs on movs.codigo like p1.codigo||"%" ' ||
' where rp.pucid=p1.pucid group by 1,2,3,4)a    on (dr.derepniifid=a.derepniifid) '||
' where dr.reportid=r.reportid    and dr.item="'||vitemm||'" and r.reportid='||vreporteid||'  group by 1,2,3,4,5,6';

EXECUTE STATEMENT QGeneral    into :repdescrip,:item, :itemdescrip,:notas,:itemcalcula,:ITEMACUMULA,:valor;

insert INTO reporten(item,descripreporte,descripitem,notas,valoract,valorant,calcula,acumula) values(:item,:repdescrip,:itemdescrip,:notas,:valor,:valor_ant,:itemcalcula,:ITEMACUMULA);
end
else
begin
if (vcalcula="S") then
begin
i=1;
j=1;
vvalorcalc=0;
vsigno="+";
while (i <= (char_length(vcalc)+1)) do
begin
if ((substring(vcalc from i for 1)="+") or (substring(vcalc from i for 1)="-") or (substring(vcalc from i for 1)="")) then
begin
vitecalcula=substring(vcalc from j for i-j);
select abs(valoract) from reporten where item=:vitecalcula into :vvalact;

if(vvalact is null) then
vvalact=0;

if (vsigno="+") then
begin
vvalorcalc=vvalorcalc+vvalact;
end

if (vsigno="-") then
begin
vvalorcalc=vvalorcalc-vvalact;
end

if (vsigno="-") then
begin
update reporten set valoract=(valoract*(-1)) where item=:vitecalcula;
end

vsigno=substring(vcalc from i for 1);
i=i+1;
j=i;
end
else
i=i+1;
end

select r.descripcion, dr.item,dr.descrip,dr.notas,dr.calcula,dr.acumula,dr.porcentaje from dereporteniif dr, reporteniif r where dr.reportid=r.reportid and item=:vitemm
and reportid=:vreporteid into :repdescrip,:item,:itemdescrip,:notas,:itemcalcula,:itemacumula,:VPORCENTAJE;
valor=vvalorcalc;

if (vporcentaje>0) then
begin
valor=(valor * vporcentaje)/100;
end

insert INTO reporten(item,descripreporte,descripitem,notas,valoract,valorant,calcula,acumula) values(:item,:repdescrip,:itemdescrip,:notas,:valor,:valor_ant,:itemcalcula,:itemacumula);

end
else
begin
select r.descripcion, item,descrip,notas,dr.calcula,0 valor,0 valor_ant,dr.acumula from dereporteniif dr, reporteniif r
where dr.reportid=r.reportid and item=:vitemm and reportid=:vreporteid into :repdescrip, :item, :itemdescrip,:notas,:itemcalcula,:valor,:valor_ant,:itemacumula;
insert INTO reporten(item,descripreporte,descripitem,notas,valoract,valorant,calcula,acumula) values(:item,:repdescrip,:itemdescrip,:notas,:valor,:valor_ant,:itemcalcula,:itemacumula);
end
end
end

for SELECT descripreporte, item, descripitem, notas,calcula, valoract, valorant,acumula from reporten into :repdescrip,:item,:itemdescrip,:notas,:itemcalcula,:valor,:valor_ant,:itemacumula do
begin
suspend;
end
END^


